INSERT INTO `wp_postmeta` VALUES("797", "202", "mfn-page-items-seo", "<br /><h2>FULL SERVICE FBO </h2><br />\n\nno-repeat;center top;;\n\nauto\n\n<p>Harvey Field <br />\r\nFAA Identifier: S43<br />\r\nField Elevation: 22 feet Mean Sea Level (MSL)<br />\r\nUnicom: 123.0<br /><br />\r\n<br />\r\nHarvey Field is served by two runways. The asphalt runway (RWY 33R/15L) is 2671 ft, in length and the parallel grass runway (RWY 33L/15R is 2430 ft, in length. All primary and secondary taxiways are paved with reflector illumination. The lights on RWY 33R/15L are illuminated from dusk to dawn. The Harvey Unicom advisory (123.0) is attended from 7:30 AM until dusk, 7 days a week.<br /><br />\r\n\r\nThe airport is within walking distance of downtown Snohomish, a city with historic character and many antique and specialty shops, great Seafood and Mexican restaurants, several Art Galleries and a river walk trail through the historic downtown district.</p>\n\nno-repeat;center top;;\n\nauto\n\n");
