INSERT INTO `wp_postmeta` VALUES("856", "216", "mfn-page-items-seo", "<h2>SCENIC FLIGHTS, TOURS AND CHARTER\r\n</h2>\r\n<h5>Helicopters and Airplanes\r\n</h5>\r\n<a href=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/Rack-Lets-Go-Flying-3-sheet-2014-02-03.pdf\">(click here for flyer)</a>\r\n<p>Snohomish Flying Service offers On-Demand Charters throughout the United States and Canada. Our charter fleet is maintained to the highest standards in our full-service general aviation maintenance facility. </p>\n\nno-repeat;center top;;\n\nauto\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webn269sfIMG_3236-compressed.jpg\" alt=\"webn269sfIMG_3236-compressed\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webn1850n_MG_3915-compressed.jpg\" alt=\"webn1850n_MG_3915-compressed\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webn491sfIMG_3593-compressed.jpg\" alt=\"webn491sfIMG_3593-compressed\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webn269tc_n269sfIMG_3226-compressed.jpg\" alt=\"webn269tc_n269sfIMG_3226-compressed\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webN7356S_05.jpg\" alt=\"webN7356S_05\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/webN1958Q_1jpg_20140115_210118_OVE_T-min.jpg\" alt=\"webN1958Q_1jpg_20140115_210118_OVE_T-min\"/>\n\n<br /><p>Fly over picturesque Mt. Rainier, Mt. St. Helens, or the Olympic Peninsula. Wherever you want to go, sit back and relax while our skilled professional pilots take you on an exciting Scenic or Photo Flight.<br />\r\n\r\nBring your camera and your friends, we can accommodate up to three persons per aircraft, and let our professional certified pilots be your guide.\r\n</p>\n\nno-repeat;center top;;\n\nauto\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/new_york_city.jpg\" alt=\"new_york_city\"/>\n\n<img src=\"http://03d61b6.netsolhost.com/sfsbeta/wp-content/uploads/2017/02/mountain.jpg\" alt=\"mountain\"/>\n\n<h4>AIRCRAFT / TIME	\r\n</h4><br /><br />Airplane - 1/2 hour Scenic Flight	<br />\r\nAirplane - 1 hour Scenic Flight	<br />\r\nHelicopter - 1/2 Hour Scenic Flight<br />\r\nHelicopter - 1 Hour Scenic Flight	<br />\r\n<i>Prices subject to change without notice.	 </i>\n\nno-repeat;center top;;\n\nauto\n\n<h4>Price\r\n</h4><br />	\r\n<br />$145 (for 1-3 people)<br />\r\n	$275 (for 1-3 people)<br />\r\n	$195 (for 1-2 people)<br />\r\n$375 (for 1-2 people)<br />\n\nno-repeat;center top;;\n\nauto\n\n");
